overview.example:
			The following example makes a backup of a database to a single file and display server output on Console.
				
FbBackup backupSvc = new FbBackup();

backupSvc.UserName = "SYSDBA";
backupSvc.UserPassword 	= "masterkey";

backupSvc.Database = @"C:\TESTDB.GDB";
backupSvc.BackupFiles.Add(new FbBackupFile(@"C:\TESTDB.GBK", 2048));
backupSvc.Verbose = true;

backupSvc.Options = FbBackupFlags.IgnoreLimbo;

backupSvc.Start();

string lineOutput;
while((lineOutput = backupSvc.GetNextLine()) != null)
{
	Console.WriteLine(lineOutput);
}

backupSvc.Close();
